import pb from "lib/pocketbase";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import io from "socket.io-client";
import { Container, Row, Col, Form, Button } from "react-bootstrap";

export default function Auth() {
  const [isLoading, setIsLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(pb.authStore.isValid);
  const { register, handleSubmit } = useForm();
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  pb.autoCancellation(false);

  const messageHandler = (messageData) => {
    setMessages((prevState) => [
      ...prevState,
      { id: messageData.id, ...messageData.field1 },
    ]);
  };

  useEffect(() => {
    async function fetchMessages() {
      try {
        const fetchedMessages = await pb.collection("messages").getFullList({
          sort: "created",
        });
        setMessages(fetchedMessages || []);
      } catch (error) {
        alert(error);
      }
    }

    fetchMessages();

    socket.on("message", messageHandler);

    return () => {
      socket.off("message", messageHandler);
    };
  }, [socket]);

  async function login(data) {
    setIsLoading(true);
    try {
      const authData = await pb
        .collection("users")
        .authWithPassword(data.email, data.password);
      setIsLoading(false);
      setIsLoggedIn(true);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  function logout() {
    pb.authStore.clear();
    setIsLoggedIn(false);
  }

  async function sendMessage() {
    if (!message) return;
    setIsLoading(true);
    try {
      const messageData = { field1: message };
      const response = await pb.collection("messages").create(messageData);
      setMessage("");
      setIsLoading(false);
      setMessages((prevState) => [
        ...prevState,
        { id: response.id, ...messageData.field1 },
      ]);
      socket.emit("message", response);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  if (isLoggedIn) {
    return (
      <div className="container text-center">
        <h1 className="my-4">Logged In: {pb.authStore.model.email}</h1>
        <button className="btn btn-danger mb-3" onClick={logout}>
          Logout
        </button>
        <h2 className="mb-3">Global Chat</h2>
        <div className="bg-light p-3 rounded mb-3">
          {messages.map((doc) => (
            <div key={doc.id}>{doc.field1}</div>
          ))}
        </div>
        <Form
          onSubmit={(e) => {
            e.preventDefault();
            sendMessage();
          }}
        >
          <div className="row">
            <div className="col-9">
              <Form.Control
                type="text"
                placeholder="Type a message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
              />
            </div>
            <div className="col-3">
              <Button
                type="submit"
                variant="primary"
                className="w-100"
                disabled={isLoading}
              >
                {isLoading ? "Sending..." : "Send"}
              </Button>
            </div>
          </div>
        </Form>
      </div>
    );
  }

  return (
    <div className="container text-center">
      {isLoading && <h1>Loading...</h1>}
      <h1 className="my-4">Please Log In</h1>
      <Form onSubmit={handleSubmit(login)}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            {...register("email")}
          />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            {...register("password")}
          />
        </Form.Group>

        <Button
          variant="primary"
          type="submit"
          disabled={isLoading}
          className="w-100"
        >
          {isLoading ? "Loading..." : "Login"}
        </Button>
      </Form>
    </div>
  );
}
