import pb from "lib/pocketbase";
import { useState, useEffect, useRef } from "react";
import { useForm } from "react-hook-form";
import "./index.css";
import io from "socket.io-client";

export default function Auth() {
  const [isLoading, setIsLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(pb.authStore.isValid);
  const { register, handleSubmit } = useForm();
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const socketRef = useRef();

  pb.autoCancellation(false);

  useEffect(() => {
    async function fetchMessages() {
      try {
        const fetchedMessages = await pb.collection("messages").getFullList({
          sort: "-created",
        });
        setMessages(fetchedMessages || []);
      } catch (error) {
        alert(error);
        console.log(error);
        console.log(error.isAbort);
      }
    }

    fetchMessages();

    // Create a Socket.IO connection
    const socket = io("http://localhost:3001");
    socketRef.current = socket;

    // Listen for incoming messages
    socket.on("message", (message) => {
      setMessages((prevState) => [...prevState, message]);
    });

    // Cleanup function to close the Socket.IO connection
    return () => {
      socket.disconnect();
    };
  }, []);

  async function login(data) {
    setIsLoading(true);
    try {
      const authData = await pb
        .collection("users")
        .authWithPassword(data.email, data.password);
      setIsLoading(false);
      setIsLoggedIn(true);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  function logout() {
    pb.authStore.clear();
    setIsLoggedIn(false);
  }

  async function sendMessage() {
    if (!message) return;
    setIsLoading(true);
    try {
      const messageData = { field1: message };
      const response = await pb.collection("messages").create(messageData);
      setMessage("");
      setIsLoading(false);

      // Emit the new message to the server
      socketRef.current.emit("message", {
        id: response.id,
        ...messageData.field1,
      });
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  if (isLoggedIn) {
    return (
      <div className="flex flex-col items-center justify-center">
        <h1 className="text-3xl font-bold mb-6">
          Logged In: {pb.authStore.model.email}
        </h1>
        <button
          onClick={logout}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
        >
          Logout
        </button>

        <h1 className="text-2xl font-bold mt-8 mb-4">Global Chat</h1>
        <form onSubmit={sendMessage} className="mt-8">
          <div className="flex flex-col">
            <input
              type="text"
              placeholder="Type a message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="border border-gray-400 rounded py-2 px-3 mb-2"
            />
            <button
              type="submit"
              disabled={isLoading}
              className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
            >
              {isLoading ? "Sending..." : "Send"}
            </button>
          </div>
        </form>
        <div className="bg-gray-200 p-4 rounded">
          {messages.map((doc) => (
            <div key={doc.id} className="mb-2">
              <span className="font-bold mr-2">{doc.field1}</span>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <>
      {isLoading && <h1>Loading...</h1>}
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit(login)}>
        <input type="text" placeholder="email" {...register("email")} />
        <input
          type="password"
          placeholder="password"
          {...register("password")}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Loading..." : "Login"}
        </button>
      </form>
    </>
  );
}
