import pb from "lib/pocketbase";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

export default function Auth() {
  const [isLoading, setIsLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(pb.authStore.isValid);
  const { register, handleSubmit } = useForm();
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState(""); // declare currentMessage state variable

  useEffect(() => {
    async function fetchMessages() {
      try {
        const fetchedMessages = await pb.collection("messages").fetchAll();
        setMessages(fetchedMessages);
      } catch (error) {
        alert(error);
      }
    }
    fetchMessages();
  }, []);

  async function login(data) {
    setIsLoading(true);
    try {
      const authData = await pb.collection("users").authWithPassword(data.email, data.password);
      setIsLoading(false);
      setIsLoggedIn(true);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  function logout() {
    pb.authStore.clear();
    setIsLoggedIn(false);
  }

  async function sendMessage() {
    if (!currentMessage) return;
    setIsLoading(true);
    try {
      const messageData = { message: currentMessage };
      await pb.collection("messages").create(messageData);
      setCurrentMessage("");
      setIsLoading(false);
      // Add the new message to the messages state
      setMessages((prevState) => [...prevState, messageData]);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  if (isLoggedIn) {
    return (
      <>
        <h1>Logged In: {pb.authStore.model.email}</h1>
        <button onClick={logout}>Logout</button>
        <h2>Global Chat</h2>
        <div>
          {messages.map((doc) => (
            <div key={doc.id}>{doc.message}</div>
          ))}
        </div>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            sendMessage();
          }}
        >
          <input
            type="text"
            placeholder="Type a message"
            value={currentMessage}
            onChange={(e) => setCurrentMessage(e.target.value)} // update currentMessage state
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? "Sending..." : "Send"}
          </button>
        </form>
        {currentMessage && (
          <div style={{ backgroundColor: 'gray' }}>
            You typed: {currentMessage}
          </div>
        )}
      </>
    );
  }


  return (
    <>
      {isLoading && <h1>Loading...</h1>}
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit(login)}>
        <input type="text" placeholder="email" {...register("email")} />
        <input type="password" placeholder="password" {...register("password")} />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Loading..." : "Login"}
        </button>
      </form>
    </>
  );
}
