import pb from "lib/pocketbase";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { io } from 'socket.io-client';

export default function Auth() {
  const [isLoading, setIsLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(pb.authStore.isValid);
  const { register, handleSubmit } = useForm();
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null); // declare socket as state
  pb.autoCancellation(false);

  useEffect(() => {
    const newSocket = io('http://localhost:3000');

    newSocket.on('message', (message) => {
      setMessages((prevState) => [      ...prevState,      { id: message.id, ...message.field1 },    ]);
    });

    setSocket(newSocket); // set the socket state
    return () => {
      newSocket.disconnect();
    };
  }, []);

  useEffect(() => {
    if (!socket) return;

    socket.on('message', (message) => {
      setMessages((prevState) => [...prevState, { id: message.id, ...message.field1 }]);
    });

    return () => {
      socket.off('message');
    };
  }, [socket]);

  useEffect(() => {
    const unsubscribe = pb.collection("messages").onSnapshot((snapshot) => {
      const fetchedMessages = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMessages(fetchedMessages || []);
    });

    return () => unsubscribe();
  }, []);

  async function login(data) {
    setIsLoading(true);
    try {
      const authData = await pb.collection("users").authWithPassword(
        data.email,
        data.password
      );
      setIsLoading(false);
      setIsLoggedIn(true);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  function logout() {
    pb.authStore.clear();
    setIsLoggedIn(false);
  }

  async function sendMessage() {
    if (!message) return;
    setIsLoading(true);
    try {
      const messageData = { field1: message };
      const response = await pb.collection('messages').create(messageData);
      setMessage('');
      setIsLoading(false);
      socket.emit('message', { id: response.id, ...messageData }); // use socket state here
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }


  if (isLoggedIn) {
    return (
      <>
        <h1>Logged In: {pb.authStore.model.email}</h1>
        <button onClick={logout}>Logout</button>
        <h2>Global Chat</h2>
        <div>
          {messages.map((doc) => (
            <div key={doc.id}>{doc.field1}</div>
          ))}
        </div>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            sendMessage();
          }}
        >
          <input
            type="text"
            placeholder="Type a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? "Sending..." : "Send"}
          </button>
        </form>
      </>
    );
  }

  return (
    <>
      {isLoading && <h1>Loading...</h1>}
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit(login)}>
        <input type="text" placeholder="email" {...register("email")} />
        <input
          type="password"
          placeholder="password"
          {...register("password")}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Loading..." : "Login"}
        </button>
      </form>
    </>
  );
}
