import pb from "lib/pocketbase";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import io from "socket.io-client";
import { Container, Row, Col, Form, Button } from "react-bootstrap";


export default function Auth() {
  const [isLoading, setIsLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(pb.authStore.isValid);
  const { register, handleSubmit } = useForm();
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const socket = io(process.env.REACT_APP_SOCKET_URL, {
    transports: ["polling"],
  });

  pb.autoCancellation(false);

  const messageHandler = (messageData) => {
    setMessages((prevState) => [
      ...prevState,
      { id: messageData.id, ...messageData.field1 },
    ]);
  };

  useEffect(() => {
    async function fetchMessages() {
      try {
        const fetchedMessages = await pb.collection("messages").getFullList({
          sort: "created",
        });
        setMessages(fetchedMessages || []);
      } catch (error) {
        alert(error);
      }
    }

    fetchMessages();

    socket.on("message", messageHandler);

    return () => {
      socket.off("message", messageHandler);
    };
  }, [socket]);

  async function login(data) {
    setIsLoading(true);
    try {
      // const authData = await pb.collection("users").authWithPassword(
      //   data.email,
      //   data.password
      // );
      setIsLoading(false);
      setIsLoggedIn(true);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  function logout() {
    pb.authStore.clear();
    setIsLoggedIn(false);
  }

  async function sendMessage() {
    if (!message) return;
    setIsLoading(true);
    try {
      const messageData = { field1: message };
      const response = await pb.collection("messages").create(messageData);
      setMessage("");
      setIsLoading(false);
      setMessages((prevState) => [
        ...prevState,
        { id: response.id, ...messageData.field1 },
      ]);
      socket.emit("message", response);
    } catch (error) {
      alert(error);
      setIsLoading(false);
    }
  }

  if (isLoggedIn) {
    return (
      <div className="container">
        <h1>Logged In: {pb.authStore.model.email}</h1>
        <button className="btn btn-primary" onClick={logout}>
          Logout
        </button>
        <h2>Global Chat</h2>
        <div>
          {messages.map((doc) => (
            <div key={doc.id}>{doc.field1}</div>
          ))}
        </div>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            sendMessage();
          }}
        >
          <input
            type="text"
            className="form-control"
            placeholder="Type a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button
            type="submit"
            className="btn btn-primary"
            disabled={isLoading}
          >
            {isLoading ? "Sending..." : "Send"}
          </button>
        </form>
      </div>
    );
  }

  return (
    <div className="container">
      {isLoading && <h1>Loading...</h1>}
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit(login)}>
        <input
          type="text"
          className="form-control"
          placeholder="email"
          {...register("email")}
        />
        <input
          type="password"
          className="form-control"
          placeholder="password"
          {...register("password")}
        />
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? "Loading..." : "Login"}
        </button>
      </form>
    </div>
  );
}
